import styled from 'styled-components';

export const Fundo = styled.div`
background: #0E7A8F;
width: 100vw;
min-height: 100vh;

 display: flex;
flex-direction: column;
justify-content: space-between;

gap: 15px;
`

export const Img = styled.img`
    height: 33vh;
    width: 100%;
`

export const Texto1 = styled.h1`
    color: #D4F1F4;
    font-family: Poppins;
    text-align: center;
    font-size: 24px;
    /* width: 80vw; */
    padding: 0 60px;
    justify-self:center;

`

export const Texto2 = styled.div`
  font-family: Poppins;
  /* width: 80vw; */
  color: white;
  display: flex;
  align-items: center;
  justify-content: center;
`;

export const ListaOrdenada = styled.ol`
  width: 80vw;
  text-align: justify;
`

export const ListaLi = styled.li`
font-size: 18px;
text-shadow: 1px 1px 0.5px black;

`
