import styled from 'styled-components';

export const Fundo = styled.div`
    background: #0E7A8F;
    width: 100%;
    height: 200vh;
`

export const CardLogin = styled.div`
    background: #0E7A8F;
    width: 80%;
    min-height: 40vh;
    padding: 1rem;
    border-radius: 12px;
    margin: 2px 22px 0;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
`
export const Imagem = styled.img`
    min-height: 35vh;
    width: 90%;
    margin-left:3vh;
    position:center;

`
export const Estilização = styled.div`
    width: 120%;
    margin-top: 0vh;
    margin-left:8%;
    align-items: 70px;

    height: 120%;
`

export const Label = styled.div`
    position: absolute;
    left: 175px;
    top:30px;
    width:50%;

`
