import styled from 'styled-components';

export const Fundo = styled.div`
    background: #05445E;
    width: 100vw;
    height: 100vh;
    display: flex;
    flex-direction: column;
`
export const Img1 = styled.img`
    width: 100vw;
    height: 30vh;

`

export const Button = styled.button`
display:none;
`

export const Titulo = styled.div`
    width: 80%;
    color: #D4F1F4;
    font-size: 20px; 
    font-family: 'Inika', sans-serif; 
    font-weight: bold; 
    margin-left: 7vw;
    display: flex;
    margin-top: 10%;

    align-items: center;
    justify-content: center;
`;

export const CardM = styled.div`
    background: #D4F1F4;
    width: 42vh;
    height: 9vh;
    border-radius: 10px;
    padding: 1rem;
    margin-left: 2vh;
    margin-top: 35%;
    display: flex;

`

export const Img = styled.div`

`
